package Pattern;

public class Pattern7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=6;
		for(int i=1;i<n;i++) {
			for(int j=i;j<n;j++)
			{System.out.print(" ");
			
			}
			for(int j=i;j<=i*2-1;j++)
			{System.out.print(i+" ");
			
			}
			System.out.println();
		}

		}

	}

	